import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import ThemeToggle from './ThemeToggle';
import './PortfolioTemplate.css';

function PortfolioTemplate() {
  const [portfolio, setPortfolio] = useState(null);
  const [loading, setLoading] = useState(true);
  const [isDarkTheme, setIsDarkTheme] = useState(false);
  const { portfolioUrl } = useParams();
  const [imageLoading, setImageLoading] = useState({});

  // Load theme preference from localStorage
  useEffect(() => {
    const savedTheme = localStorage.getItem('theme');
    if (savedTheme === 'dark') {
      setIsDarkTheme(true);
      document.documentElement.setAttribute('data-theme', 'dark');
    }
  }, []);

  // Handle theme toggle
  const toggleTheme = () => {
    setIsDarkTheme(!isDarkTheme);
    document.documentElement.setAttribute(
      'data-theme',
      isDarkTheme ? 'light' : 'dark'
    );
    localStorage.setItem('theme', isDarkTheme ? 'light' : 'dark');
  };

  useEffect(() => {
    const fetchPortfolio = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/portfolio/${portfolioUrl}`);
        console.log('Fetched portfolio data:', response.data.portfolio); // Debug log
        setPortfolio(response.data.portfolio);
      } catch (error) {
        console.error('Error fetching portfolio:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchPortfolio();
  }, [portfolioUrl]);

  if (loading) return <div className="loading">Loading...</div>;
  if (!portfolio) return <div className="error">Portfolio not found</div>;

  return (
    <div className="portfolio-container">
      <ThemeToggle isDark={isDarkTheme} onToggle={toggleTheme} />
      <header className="portfolio-header">
        {portfolio.profileImage && (
          <img 
            src={`http://localhost:5000/uploads/${portfolio.profileImage}`}
            alt={portfolio.name}
            className="profile-image"
            onError={(e) => {
              e.target.onerror = null; // Prevent infinite loop
              e.target.src = '/default-profile.png'; // Set a default image
            }}
          />
        )}
        <h1>{portfolio.name}</h1>
        <p className="bio">{portfolio.bio}</p>
      </header>

      <section className="skills-section">
        <h2>Skills</h2>
        <div className="skills-grid">
          {portfolio.skills.map((skill, index) => (
            <div key={index} className="skill-item">
              {skill}
            </div>
          ))}
        </div>
      </section>

      <section className="projects-section">
        <h2>Projects</h2>
        <div className="projects-grid">
          {portfolio.projects.map((project, index) => (
            <div key={index} className="project-card">
              <div className={`project-image-container ${imageLoading[index] ? 'loading' : ''}`}>
                <img
                  src={`http://localhost:5000/uploads/${project.image}`}
                  alt={project.title}
                  className="project-image"
                  onLoad={() => setImageLoading(prev => ({ ...prev, [index]: false }))}
                  onError={(e) => {
                    e.target.onerror = null;
                    e.target.src = '/default-project.png';
                    setImageLoading(prev => ({ ...prev, [index]: false }));
                  }}
                />
              </div>
              <div className="project-content">
                <h3>{project.title}</h3>
                <p className="project-description">{project.description}</p>
                <div className="project-technologies">
                  {Array.isArray(project.technologies) ? (
                    project.technologies.map((tech, i) => (
                      <span key={i} className="tech-tag">{tech}</span>
                    ))
                  ) : (
                    <span className="tech-tag">{project.technologies}</span>
                  )}
                </div>
                {project.link && (
                  <a 
                    href={project.link} 
                    target="_blank" 
                    rel="noopener noreferrer" 
                    className="project-link"
                  >
                    <span>View Project</span>
                  </a>
                )}
              </div>
            </div>
          ))}
        </div>
      </section>

      <section className="contact-section">
        <h2>Contact</h2>
        <div className="contact-links">
          {portfolio.contact.github && (
            <a href={portfolio.contact.github} target="_blank" rel="noopener noreferrer">
              GitHub
            </a>
          )}
          {portfolio.contact.linkedin && (
            <a href={portfolio.contact.linkedin} target="_blank" rel="noopener noreferrer">
              LinkedIn
            </a>
          )}
          {portfolio.email && (
            <a href={`mailto:${portfolio.email}`}>
              Email
            </a>
          )}
        </div>
      </section>

      {portfolio.resume && (
        <section className="resume-section">
          <h2>Resume</h2>
          <a 
            href={`http://localhost:5000/uploads/${portfolio.resume}`}
            target="_blank"
            rel="noopener noreferrer"
            className="resume-link"
          >
            Download Resume
          </a>
        </section>
      )}
    </div>
  );
}

export default PortfolioTemplate;
