import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import './PortfolioTemplate.css';

const PortfolioTemplate = () => {
  const [portfolio, setPortfolio] = useState(null);
  const [loading, setLoading] = useState(true);
  const { portfolioUrl } = useParams();

  useEffect(() => {
    const fetchPortfolio = async () => {
      try {
        const response = await axios.get(`/api/portfolio/${portfolioUrl}`);
        setPortfolio(response.data.portfolio);
      } catch (error) {
        console.error('Error fetching portfolio:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchPortfolio();
  }, [portfolioUrl]);

  if (loading) return <div>Loading...</div>;
  if (!portfolio) return <div>Portfolio not found</div>;

  return (
    <div className="portfolio-container">
      <header className="portfolio-header">
        <img src={portfolio.user.profileImage} alt={portfolio.user.name} className="profile-image" />
        <h1>{portfolio.user.name}</h1>
        <p className="bio">{portfolio.user.bio}</p>
      </header>

      <section className="skills-section">
        <h2>Skills</h2>
        <div className="skills-grid">
          {portfolio.skills.map((skill, index) => (
            <div key={index} className="skill-item">
              <img src={skill.logo} alt={skill.name} />
              <span>{skill.name}</span>
            </div>
          ))}
        </div>
      </section>

      <section className="projects-section">
        <h2>Projects</h2>
        <div className="projects-grid">
          {portfolio.projects.map((project, index) => (
            <div key={index} className="project-card">
              <img src={project.projectImage} alt={project.title} />
              <h3>{project.title}</h3>
              <p>{project.description}</p>
              <a href={project.projectLink} target="_blank" rel="noopener noreferrer">
                View Project
              </a>
            </div>
          ))}
        </div>
      </section>

      <footer className="contact-section">
        <h2>Contact</h2>
        <div className="contact-links">
          {portfolio.contact.github && (
            <a href={portfolio.contact.github} target="_blank" rel="noopener noreferrer">
              GitHub
            </a>
          )}
          {portfolio.contact.linkedin && (
            <a href={portfolio.contact.linkedin} target="_blank" rel="noopener noreferrer">
              LinkedIn
            </a>
          )}
          <a href={`mailto:${portfolio.contact.email}`}>Email</a>
        </div>
      </footer>
    </div>
  );
};

export default PortfolioTemplate;
