import React, { useState } from 'react';
import './ThemePanel.css';

const ThemePanel = ({ onThemeChange, onFontChange, onBgImageChange }) => {
  const [isPanelOpen, setIsPanelOpen] = useState(false);

  const themes = [
    { name: 'Default', colors: { primary: '#4834d4', secondary: '#ff6b6b' } },
    { name: 'Ocean', colors: { primary: '#0099ff', secondary: '#00ff99' } },
    { name: 'Sunset', colors: { primary: '#ff9966', secondary: '#ff5e62' } },
    { name: 'Forest', colors: { primary: '#134e5e', secondary: '#71b280' } },
  ];

  const fonts = [
    { name: 'Poppins', value: "'Poppins', sans-serif" },
    { name: 'Roboto', value: "'Roboto', sans-serif" },
    { name: 'Montserrat', value: "'Montserrat', sans-serif" },
    { name: 'Open Sans', value: "'Open Sans', sans-serif" },
  ];

  const handleImageUpload = async (e) => {
    const file = e.target.files[0];
    if (file) {
      const formData = new FormData();
      formData.append('heroImage', file);
      
      try {
        const response = await fetch('http://localhost:5000/api/upload-hero-image', {
          method: 'POST',
          body: formData
        });
        const data = await response.json();
        onBgImageChange(data.imageUrl);
      } catch (error) {
        console.error('Error uploading image:', error);
      }
    }
  };

  return (
    <div className={`theme-panel ${isPanelOpen ? 'open' : ''}`}>
      <button 
        className="panel-toggle"
        onClick={() => setIsPanelOpen(!isPanelOpen)}
      >
        <i className="fas fa-paint-brush"></i>
      </button>

      <div className="panel-content">
        <h3>Customize Theme</h3>
        
        <div className="section">
          <h4>Color Themes</h4>
          <div className="theme-options">
            {themes.map((theme) => (
              <button
                key={theme.name}
                className="theme-option"
                onClick={() => onThemeChange(theme.colors)}
                style={{
                  background: `linear-gradient(45deg, ${theme.colors.primary}, ${theme.colors.secondary})`
                }}
              >
                <span>{theme.name}</span>
              </button>
            ))}
          </div>
        </div>

        <div className="section">
          <h4>Fonts</h4>
          <div className="font-options">
            {fonts.map((font) => (
              <button
                key={font.name}
                className="font-option"
                onClick={() => onFontChange(font.value)}
                style={{ fontFamily: font.value }}
              >
                {font.name}
              </button>
            ))}
          </div>
        </div>

        <div className="section">
          <h4>Hero Background</h4>
          <div className="image-upload">
            <label htmlFor="hero-image">
              <i className="fas fa-upload"></i>
              Upload Image
            </label>
            <input
              type="file"
              id="hero-image"
              accept="image/*"
              onChange={handleImageUpload}
              style={{ display: 'none' }}
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default ThemePanel;
