import React, { useState } from 'react';
import './ThemePanel.css';

const ThemePanel = ({ onThemeChange }) => {
  const [isPanelOpen, setIsPanelOpen] = useState(false);

  const themes = [
    {
      name: 'Classic',
      colors: {
        primary: '#4834d4',
        secondary: '#686de0',
        background: '#ffffff',
        text: '#2d3436'
      },
      font: 'Poppins',
      backgroundImage: 'gradient'
    },
    {
      name: 'Dark Mode',
      colors: {
        primary: '#6c5ce7',
        secondary: '#a55eea',
        background: '#2d3436',
        text: '#ffffff'
      },
      font: 'Roboto',
      backgroundImage: 'dark-pattern'
    },
    {
      name: 'Minimalist',
      colors: {
        primary: '#0984e3',
        secondary: '#74b9ff',
        background: '#f5f6fa',
        text: '#2d3436'
      },
      font: 'Inter',
      backgroundImage: 'minimal'
    },
    {
      name: 'Nature',
      colors: {
        primary: '#00b894',
        secondary: '#55efc4',
        background: '#ffffff',
        text: '#2d3436'
      },
      font: 'Montserrat',
      backgroundImage: 'nature'
    }
  ];

  const fonts = [
    'Poppins',
    'Roboto',
    'Montserrat',
    'Inter',
    'Open Sans',
    'Playfair Display'
  ];

  const backgrounds = [
    { name: 'Gradient', value: 'gradient' },
    { name: 'Waves', value: 'waves' },
    { name: 'Geometric', value: 'geometric' },
    { name: 'Minimal', value: 'minimal' }
  ];

  return (
    <div className={`theme-panel ${isPanelOpen ? 'open' : ''}`}>
      <button 
        className="theme-panel-toggle"
        onClick={() => setIsPanelOpen(!isPanelOpen)}
      >
        <i className="fas fa-palette"></i>
      </button>

      <div className="theme-panel-content">
        <h3>Customize Theme</h3>
        
        <div className="theme-section">
          <h4>Color Themes</h4>
          <div className="theme-options">
            {themes.map((theme) => (
              <button
                key={theme.name}
                className="theme-option"
                onClick={() => onThemeChange('theme', theme)}
              >
                <div 
                  className="color-preview" 
                  style={{ background: theme.colors.primary }}
                ></div>
                <span>{theme.name}</span>
              </button>
            ))}
          </div>
        </div>

        <div className="theme-section">
          <h4>Fonts</h4>
          <div className="font-options">
            {fonts.map((font) => (
              <button
                key={font}
                className="font-option"
                style={{ fontFamily: font }}
                onClick={() => onThemeChange('font', font)}
              >
                {font}
              </button>
            ))}
          </div>
        </div>

        <div className="theme-section">
          <h4>Background Style</h4>
          <div className="background-options">
            {backgrounds.map((bg) => (
              <button
                key={bg.value}
                className="background-option"
                onClick={() => onThemeChange('background', bg.value)}
              >
                <div className={`bg-preview ${bg.value}`}></div>
                <span>{bg.name}</span>
              </button>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ThemePanel;
