import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './PortfolioTemplate.css';

function PortfolioTemplate() {
  const [portfolio, setPortfolio] = useState(null);
  const [loading, setLoading] = useState(true);
  const { portfolioUrl } = useParams();

  useEffect(() => {
    const fetchPortfolio = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/portfolio/${portfolioUrl}`);
        setPortfolio(response.data.portfolio);
      } catch (error) {
        console.error('Error fetching portfolio:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchPortfolio();
  }, [portfolioUrl]);

  if (loading) return <div className="loading">Loading...</div>;
  if (!portfolio) return <div className="error">Portfolio not found</div>;

  return (
    <div className="portfolio-container">
      <header className="portfolio-header">
        {portfolio.profileImage && (
          <img 
            src={`http://localhost:5000/uploads/${portfolio.profileImage}`}
            alt={portfolio.name}
            className="profile-image"
            onError={(e) => {
              e.target.onerror = null; // Prevent infinite loop
              e.target.src = '/default-profile.png'; // Set a default image
            }}
          />
        )}
        <h1>{portfolio.name}</h1>
        <p className="bio">{portfolio.bio}</p>
      </header>

      <section className="skills-section">
        <h2>Skills</h2>
        <div className="skills-grid">
          {portfolio.skills.map((skill, index) => (
            <div key={index} className="skill-item">
              {skill}
            </div>
          ))}
        </div>
      </section>

      <section className="projects-section">
        <h2>Projects</h2>
        <div className="projects-content">
          <p>{portfolio.projects}</p>
        </div>
      </section>

      <section className="contact-section">
        <h2>Contact</h2>
        <div className="contact-links">
          {portfolio.contact.github && (
            <a href={portfolio.contact.github} target="_blank" rel="noopener noreferrer">
              GitHub
            </a>
          )}
          {portfolio.contact.linkedin && (
            <a href={portfolio.contact.linkedin} target="_blank" rel="noopener noreferrer">
              LinkedIn
            </a>
          )}
          {portfolio.email && (
            <a href={`mailto:${portfolio.email}`}>
              Email
            </a>
          )}
        </div>
      </section>

      {portfolio.resume && (
        <section className="resume-section">
          <h2>Resume</h2>
          <a 
            href={`http://localhost:5000/${portfolio.resume}`}
            target="_blank"
            rel="noopener noreferrer"
            className="resume-link"
          >
            Download Resume
          </a>
        </section>
      )}
    </div>
  );
}

export default PortfolioTemplate;
