import React, { useState } from 'react';
import axios from 'axios';
import './PortfolioForm.css';

const PortfolioForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    bio: '',
    skills: [],
    profileImage: null,
    projects: [],
    certificates: [],
    contact: {
      github: '',
      linkedin: '',
      email: '',
      phone: ''
    },
    resume: null
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();
    
    // Append all form data
    Object.keys(formData).forEach(key => {
      if (key === 'profileImage' || key === 'resume') {
        data.append(key, formData[key]);
      } else {
        data.append(key, JSON.stringify(formData[key]));
      }
    });

    try {
      const response = await axios.post('/api/portfolio/create', data, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      alert(`Portfolio created! Access it at: ${response.data.portfolioUrl}`);
    } catch (error) {
      console.error('Error creating portfolio:', error);
    }
  };

  const handleSkillAdd = () => {
    setFormData({
      ...formData,
      skills: [...formData.skills, { name: '', logo: null }]
    });
  };

  const handleProjectAdd = () => {
    setFormData({
      ...formData,
      projects: [...formData.projects, {
        title: '',
        description: '',
        projectLink: '',
        projectImage: null,
        technologies: []
      }]
    });
  };

  return (
    <form onSubmit={handleSubmit} className="portfolio-form">
      <h2>Create Your Portfolio</h2>
      
      {/* Basic Information */}
      <section className="form-section">
        <h3>Basic Information</h3>
        <input
          type="text"
          placeholder="Name"
          onChange={(e) => setFormData({...formData, name: e.target.value})}
        />
        <input
          type="email"
          placeholder="Email"
          onChange={(e) => setFormData({...formData, email: e.target.value})}
        />
        <textarea
          placeholder="Bio"
          onChange={(e) => setFormData({...formData, bio: e.target.value})}
        />
        <input
          type="file"
          accept="image/*"
          onChange={(e) => setFormData({...formData, profileImage: e.target.files[0]})}
        />
      </section>

      {/* Skills Section */}
      <section className="form-section">
        <h3>Skills</h3>
        {formData.skills.map((skill, index) => (
          <div key={index} className="skill-input">
            <input
              type="text"
              placeholder="Skill name"
              onChange={(e) => {
                const newSkills = [...formData.skills];
                newSkills[index].name = e.target.value;
                setFormData({...formData, skills: newSkills});
              }}
            />
            <input
              type="file"
              accept="image/*"
              onChange={(e) => {
                const newSkills = [...formData.skills];
                newSkills[index].logo = e.target.files[0];
                setFormData({...formData, skills: newSkills});
              }}
            />
          </div>
        ))}
        <button type="button" onClick={handleSkillAdd}>Add Skill</button>
      </section>

      {/* Projects Section */}
      <section className="form-section">
        <h3>Projects</h3>
        {formData.projects.map((project, index) => (
          <div key={index} className="project-input">
            <input
              type="text"
              placeholder="Project title"
              onChange={(e) => {
                const newProjects = [...formData.projects];
                newProjects[index].title = e.target.value;
                setFormData({...formData, projects: newProjects});
              }}
            />
            <input
              type="text"
              placeholder="Project link"
              onChange={(e) => {
                const newProjects = [...formData.projects];
                newProjects[index].projectLink = e.target.value;
                setFormData({...formData, projects: newProjects});
              }}
            />
            <input
              type="file"
              accept="image/*"
              onChange={(e) => {
                const newProjects = [...formData.projects];
                newProjects[index].projectImage = e.target.files[0];
                setFormData({...formData, projects: newProjects});
              }}
            />
          </div>
        ))}
        <button type="button" onClick={handleProjectAdd}>Add Project</button>
      </section>

      {/* Contact Information */}
      <section className="form-section">
        <h3>Contact Information</h3>
        <input
          type="text"
          placeholder="GitHub URL"
          onChange={(e) => setFormData({
            ...formData,
            contact: {...formData.contact, github: e.target.value}
          })}
        />
        <input
          type="text"
          placeholder="LinkedIn URL"
          onChange={(e) => setFormData({
            ...formData,
            contact: {...formData.contact, linkedin: e.target.value}
          })}
        />
      </section>

      {/* Resume Upload */}
      <section className="form-section">
        <h3>Resume</h3>
        <input
          type="file"
          accept=".pdf,.doc,.docx"
          onChange={(e) => setFormData({...formData, resume: e.target.files[0]})}
        />
      </section>

      <button type="submit">Generate Portfolio</button>
    </form>
  );
};

export default PortfolioForm;
