import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './PortfolioForm.css';

const PortfolioForm = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    bio: '',
    skills: '',
    projects: '',
    contact: {
      github: '',
      linkedin: '',
      email: '',
      phone: ''
    }
  });
  const [files, setFiles] = useState({
    profileImage: null,
    resume: null
  });
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      const formDataToSend = new FormData();

      // Append text fields
      formDataToSend.append('name', formData.name);
      formDataToSend.append('email', formData.email);
      formDataToSend.append('bio', formData.bio);
      formDataToSend.append('skills', formData.skills);
      formDataToSend.append('projects', formData.projects);
      formDataToSend.append('contact', JSON.stringify(formData.contact));

      // Append files if they exist
      if (files.profileImage) {
        formDataToSend.append('profileImage', files.profileImage);
      }
      if (files.resume) {
        formDataToSend.append('resume', files.resume);
      }

      const response = await axios.post('http://localhost:5000/api/portfolio/create', 
        formDataToSend,
        {
          headers: {
            'Content-Type': 'multipart/form-data',
          },
        }
      );

      // Navigate to the created portfolio
      navigate(response.data.portfolioUrl);
    } catch (error) {
      console.error('Error creating portfolio:', error);
      setError(error.response?.data?.error || 'Error creating portfolio. Please try again.');
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name.includes('.')) {
      const [parent, child] = name.split('.');
      setFormData(prev => ({
        ...prev,
        [parent]: {
          ...prev[parent],
          [child]: value
        }
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }
  };

  const handleFileChange = (e) => {
    const { name, files: fileList } = e.target;
    setFiles(prev => ({
      ...prev,
      [name]: fileList[0]
    }));
  };

  return (
    <div className="container">
      <h1>Create Your Portfolio</h1>
      {error && <div className="error-message">{error}</div>}
      <form onSubmit={handleSubmit} className="portfolio-form">
        <div>
          <label>Name:</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </div>

        <div>
          <label>Email:</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>

        <div>
          <label>Bio:</label>
          <textarea
            name="bio"
            value={formData.bio}
            onChange={handleChange}
            required
          />
        </div>

        <div>
          <label>Skills (comma-separated):</label>
          <input
            type="text"
            name="skills"
            value={formData.skills}
            onChange={handleChange}
            placeholder="e.g., JavaScript, React, Node.js"
            required
          />
        </div>

        <div>
          <label>Profile Image:</label>
          <input
            type="file"
            name="profileImage"
            accept="image/*"
            onChange={handleFileChange}
          />
        </div>

        <div>
          <label>Projects:</label>
          <textarea
            name="projects"
            value={formData.projects}
            onChange={handleChange}
            placeholder="Describe your projects..."
            required
          />
        </div>

        <div>
          <label>GitHub URL:</label>
          <input
            type="url"
            name="contact.github"
            value={formData.contact.github}
            onChange={handleChange}
            placeholder="https://github.com/yourusername"
          />
        </div>

        <div>
          <label>LinkedIn URL:</label>
          <input
            type="url"
            name="contact.linkedin"
            value={formData.contact.linkedin}
            onChange={handleChange}
            placeholder="https://linkedin.com/in/yourusername"
          />
        </div>

        <div>
          <label>Resume:</label>
          <input
            type="file"
            name="resume"
            accept=".pdf,.doc,.docx"
            onChange={handleFileChange}
          />
        </div>

        <button type="submit">Create Portfolio</button>
      </form>
    </div>
  );
};

export default PortfolioForm;
